import React, { useRef, useState } from "react";
import { useForm } from "react-hook-form";
import firebase from "../../firebase";

function RegisterPage() {
  const { register, watch, errors, handleSubmit } = useForm();
  const [errorFromSubmit, setErrorFromSubmit] = useState("");
  const password = useRef();
  password.current = watch("password");

  const onSubmit = async (data) => {
    try {
      let createdUser = await firebase
        .auth()
        .createUserWithEmailAndPassword(data.email, data.password);
      console.log("createdUser", createdUser);
    } catch (error) {
      setErrorFromSubmit(error.message);
    }
  };

  return (
    <div>
      <div className="max-w-xl mx-auto flex flex-col mt-40 ">
        <h2 className="mb-4 text-center">회원가입</h2>
        <form
          className="flex flex-col w-full"
          onSubmit={handleSubmit(onSubmit)}
        >
          <label className="p-2 mb-1" htmlFor="email">
            이메일
          </label>
          <input
            {...register("email", {
              required: true,
              pattern: /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/,
            })}
            className="input"
            type="email"
            id="email"
            name="email"
          />
          {/* {errors.email && <p>이메일은 입력해야 합니다.</p>} */}

          <label className="p-2 mb-1" htmlFor="userId">
            아이디
          </label>
          <input
            {...register("userId", { required: true, minLength: 6 })}
            className="input"
            type="text"
            id="userId"
            name="userId"
          />
          {errors?.userId.type === "required" && (
            <p>아이디를 입력하셔야 합니다.</p>
          )}
          {errors?.userId.type === "minLength" && (
            <p>아이디는 최소 6자 이상이어야 합니다.</p>
          )}

          <label className="p-2 mb-1" htmlFor="password">
            비밀번호
          </label>
          <input
            className="input"
            type="password"
            id="password"
            name="password"
            {...register("password", { required: true, minLength: 6 })}
          />
          {errors.password && errors.password.type === "required" && (
            <p>패스워드를 입력하셔야 합니다.</p>
          )}
          {errors.password && errors.password.type === "minLength" && (
            <p>비밀번호는 최소 6자 이상이어야 합니다.</p>
          )}

          <label className="p-2 mb-1" htmlFor="password_confirm">
            비밀번호 확인
          </label>
          <input
            className="input"
            type="password"
            id="password_confirm"
            name="password_confirm"
            {...register("password_confirm", {
              required: true,
              validate: (value) => value === password.current,
            })}
          />
          {errors.password_confrim &&
            errors.password_confrim.type === "required" && (
              <p>패스워드 확인을 입력하셔야 합니다.</p>
            )}
          {errors.password_confrim &&
            errors.password_confrim.type === "validate" && (
              <p>비밀번호와 비밀번호 확인은 일치하여야 합니다.</p>
            )}

          <input
            className="py-3 mt-4 bg-green-500 text-white font-medium hover:bg-green-600 focus:none"
            type="submit"
            value="전송하기"
          />
        </form>
      </div>
    </div>
  );
}

export default RegisterPage;
